import React, { useState } from "react";
import { useDispatch } from "react-redux";
import styled from "styled-components";
import { signup } from "reducers/user";

const Signup = styled.form`
  display: flex;
  flex-direction: column;
  margin: auto;
  width: 50%;
`;

const Label = styled.label``;

const Input = styled.input`
  width: 100%;
`;

export const Form = () => {
  const dispatch = useDispatch();

  const [inputValue, setInputValue] = useState({
    name: "",
    email: "",
    password: "",
    passwordCheck: "",
  });

  // const [name, setName] = useState("");
  // const [email, setEmail] = useState("");
  // const [password, setPassword] = useState("");
  // const [passwordCheck, setPasswordCheck] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInputValue((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSignup = (event) => {
    event.preventDefault();
    dispatch(signup(inputValue.name, inputValue.email, inputValue.password));
    setInputValue({ name: "", email: "", password: "", passwordCheck: "" });
  };
  return (
    <Signup>
      <Label>
        Name
        <Input value={inputValue.name} onChange={handleChange} />
      </Label>
      <Label>
        E-mail
        <Input value={inputValue.email} onChange={handleChange} />
      </Label>
      <Label>
        Password
        <Input
          type="password"
          value={inputValue.password}
          onChange={handleChange}
        />
      </Label>
      <Label>
        Confirm password
        <Input
          type="password"
          value={inputValue.passwordCheck}
          onChange={handleChange}
        />
      </Label>

      <button
        disabled={
          inputValue.password.length > 0 &&
          inputValue.password === inputValue.passwordCheck
            ? false
            : true
        }
        type="submit"
        onClick={handleSignup}>
        Sign up
      </button>
    </Signup>
  );
};
